package stringutube;

public class starpattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int count=0;
		  for (int c=1;c<=5; c++) {
	        	for (int d=1; d<=c;d++) {
	        		
	        	System.out.print(" "+count);
	        	count++;
	        		
	        	}
	        	System.out.println(" ");
            	}	  
}
}